import React, { useState } from "react";
import axios from "axios";

const DocumentAnalyzer = () => {
  const [file, setFile] = useState(null);
  const [extractedText, setExtractedText] = useState("");
  const [chatHistory, setChatHistory] = useState([]);
  const [userMessage, setUserMessage] = useState("");
  const [loading, setLoading] = useState(false);

  const handleFileChange = (event) => {
    setFile(event.target.files[0]);
  };

  const uploadFile = async () => {
    if (!file) {
      alert("Please select a file to upload.");
      return;
    }

    const formData = new FormData();
    formData.append("file", file);

    try {
      setLoading(true);
      const response = await axios.post("http://127.0.0.1:8000/upload", formData, {
        headers: { "Content-Type": "multipart/form-data" },
      });
      setExtractedText(response.data.extracted_text);
      setLoading(false);
    } catch (error) {
      console.error("Error uploading file:", error);
      alert("Failed to upload file. Please try again.");
      setLoading(false);
    }
  };

  const sendMessage = async () => {
    if (!userMessage) {
      alert("Please enter a message.");
      return;
    }

    const newChatHistory = [
      ...chatHistory,
      { role: "user", content: userMessage },
    ];

    try {
      setLoading(true);
      const response = await axios.post("http://127.0.0.1:8000/chat", {
        messages: newChatHistory,
      });
      const botMessage = {
        role: "assistant",
        content: response.data.response,
      };
      setChatHistory([...newChatHistory, botMessage]);
      setUserMessage("");
      setLoading(false);
    } catch (error) {
      console.error("Error interacting with AI:", error);
      alert("Failed to communicate with AI. Please try again.");
      setLoading(false);
    }
  };

  return (
    <div className="container mx-auto p-4">
      <h1 className="text-2xl font-bold mb-4">Document Analyzer</h1>

      <div className="mb-4">
        <label className="block text-sm font-medium text-gray-700 mb-2">
          Upload a file (PDF or DOCX)
        </label>
        <input
          type="file"
          accept=".pdf, .docx"
          onChange={handleFileChange}
          className="block w-full text-sm text-gray-500 border border-gray-300 rounded-lg cursor-pointer"
        />
        <button
          onClick={uploadFile}
          className="mt-2 px-4 py-2 bg-blue-500 text-white rounded hover:bg-blue-600"
        >
          Upload File
        </button>
      </div>

      {loading && <p className="text-blue-500">Processing...</p>}

      {extractedText && (
        <div className="mt-4 p-4 border rounded bg-gray-100">
          <h2 className="text-xl font-bold">Extracted Text:</h2>
          <p className="mt-2 text-gray-700 whitespace-pre-line">{extractedText}</p>
        </div>
      )}

      <div className="mt-4">
        <h2 className="text-xl font-bold mb-2">Chat with AI</h2>
        <textarea
          value={userMessage}
          onChange={(e) => setUserMessage(e.target.value)}
          rows="3"
          className="w-full border rounded p-2 mb-2"
          placeholder="Type your message here..."
        ></textarea>
        <button
          onClick={sendMessage}
          className="px-4 py-2 bg-green-500 text-white rounded hover:bg-green-600"
        >
          Send Message
        </button>
      </div>

      <div className="mt-4">
        <h2 className="text-xl font-bold mb-2">Chat History</h2>
        <div className="border rounded p-4 bg-gray-50">
          {chatHistory.map((chat, index) => (
            <div key={index} className="mb-2">
              <strong>{chat.role === "user" ? "You" : "AI"}:</strong>
              <p className="ml-2">{chat.content}</p>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default DocumentAnalyzer;
