import React, { useState } from "react";
import Dropzone from "react-dropzone";

const DocumentAnalyzer = () => {
  const [file, setFile] = useState(null);
  const [text, setText] = useState("");
  const [analysisResult, setAnalysisResult] = useState("");
  const [customQuery, setCustomQuery] = useState("");

  const handleFileDrop = (acceptedFiles) => {
    const uploadedFile = acceptedFiles[0];
    setFile(uploadedFile);

    const formData = new FormData();
    formData.append("file", uploadedFile);

    fetch("http://127.0.0.1:5000/upload", {
      method: "POST",
      body: formData,
    })
      .then((response) => response.json())
      .then((data) => {
        setAnalysisResult(data.result);
      })
      .catch((error) => console.error("Error uploading file:", error));
  };

  const handleTextSubmit = () => {
    fetch("http://127.0.0.1:5000/analyze_text", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ text, query: customQuery }),
    })
      .then((response) => response.json())
      .then((data) => {
        setAnalysisResult(data.result);
      })
      .catch((error) => console.error("Error analyzing text:", error));
  };

  return (
    <div className="p-6 max-w-4xl mx-auto bg-white shadow-lg rounded-lg">
      <h1 className="text-2xl font-bold mb-4">Document Analyzer</h1>

      <div className="flex space-x-4 mb-6">
        <button className="bg-blue-500 text-white px-4 py-2 rounded">Upload Document</button>
        <button className="bg-gray-200 text-gray-700 px-4 py-2 rounded">Enter Text</button>
      </div>

      <Dropzone onDrop={handleFileDrop}>
        {({ getRootProps, getInputProps }) => (
          <div
            {...getRootProps()}
            className="border-dashed border-2 border-gray-300 p-6 text-center cursor-pointer rounded"
          >
            <input {...getInputProps()} />
            <p>Click to upload or drag and drop</p>
            <p className="text-sm text-gray-500">Supported formats: PDF, DOCX, TXT</p>
          </div>
        )}
      </Dropzone>

      <div className="mt-4">
        <textarea
          className="w-full p-2 border rounded"
          rows="6"
          placeholder="Enter text here for analysis..."
          value={text}
          onChange={(e) => setText(e.target.value)}
        />
      </div>

      <div className="mt-4 flex flex-col">
        <label className="mb-2 font-medium">Custom Query (Optional)</label>
        <input
          type="text"
          className="p-2 border rounded"
          placeholder="Enter specific analysis question..."
          value={customQuery}
          onChange={(e) => setCustomQuery(e.target.value)}
        />
      </div>

      <button
        onClick={handleTextSubmit}
        className="mt-4 bg-blue-500 text-white px-6 py-2 rounded"
      >
        Analyze Content
      </button>

      {analysisResult && (
        <div className="mt-6 p-4 bg-gray-100 rounded">
          <h2 className="text-xl font-bold mb-2">Analysis Result:</h2>
          <p>{analysisResult}</p>
        </div>
      )}
    </div>
  );
};

export default DocumentAnalyzer;
