import React, { useState, useEffect } from "react";
import { useDropzone } from "react-dropzone";
import ReactMarkdown from 'react-markdown';

const BACKEND_URL = "http://localhost:8000"; // Update this if your backend is on a different port

const DocumentAnalyzer = () => {
  const [file, setFile] = useState(null);
  const [text, setText] = useState("");
  const [analysisResult, setAnalysisResult] = useState("");
  const [customQuery, setCustomQuery] = useState("");
  const [analysisCategory, setAnalysisCategory] = useState("summary"); // Default category
  const [isTextInput, setIsTextInput] = useState(false);
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState("");
  const [isBackendAvailable, setIsBackendAvailable] = useState(false);

  // Check backend health on component mount
  useEffect(() => {
    checkBackendHealth();
  }, []);

  async function checkBackendHealth() {
    try {
      const response = await fetch(`${BACKEND_URL}/health`);
      const data = await response.json();
      setIsBackendAvailable(data.status === "healthy");
      console.log("Backend health check:", data);
    } catch (error) {
      console.error("Backend health check failed:", error);
      setIsBackendAvailable(false);
      setError("Cannot connect to backend server. Please ensure it's running.");
    }
  }

  const { getRootProps, getInputProps } = useDropzone({
    onDrop: handleFileDrop,
    accept: {
      'application/pdf': ['.pdf'],
      'application/vnd.openxmlformats-officedocument.wordprocessingml.document': ['.docx'],
      'text/plain': ['.txt']
    },
    multiple: false
  });

  async function handleFileDrop(acceptedFiles) {
    try {
      setIsLoading(true);
      setError("");
      const uploadedFile = acceptedFiles[0];
      setFile(uploadedFile);

      console.log("Uploading file:", uploadedFile.name, uploadedFile.type);

      const formData = new FormData();
      formData.append("file", uploadedFile);
      formData.append("query", customQuery.trim() || "Analyze the document");

      // Send the file and custom query to the backend
      const response = await fetch(`${BACKEND_URL}/upload`, {
        method: "POST",
        body: formData,
      });

      console.log("Upload response status:", response.status);

      if (!response.ok) {
        const errorData = await response.json().catch(() => ({}));
        throw new Error(errorData.detail || `Upload failed: ${response.statusText}`);
      }

      // Simulate streaming data (Here, we’ll simulate it by fetching parts of the result)
      const data = await response.json();
      setAnalysisResult(data.result || data.extracted_text);
    } catch (error) {
      console.error("File upload error:", error);
      setError(`Error uploading file: ${error.message}`);
    } finally {
      setIsLoading(false);
    }
  }

  async function handleTextSubmit() {
    try {
      setIsLoading(true);
      setError("");

      if (!text.trim()) {
        throw new Error("Please enter some text to analyze");
      }

      console.log("Analyzing text, length:", text.length);

      const response = await fetch(`${BACKEND_URL}/analyze_text`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ 
          text, 
          query: customQuery.trim() || "Analyze this text and provide key insights",
          category: analysisCategory  // Send selected category to backend
        }),
      });

      console.log("Analysis response status:", response.status);

      if (!response.ok) {
        const errorData = await response.json().catch(() => ({}));
        throw new Error(errorData.detail || `Analysis failed: ${response.statusText}`);
      }

      const data = await response.json();
      console.log("Analysis response data:", data);
      setAnalysisResult(data.result);
    } catch (error) {
      console.error("Text analysis error:", error);
      setError(`Error analyzing text: ${error.message}`);
    } finally {
      setIsLoading(false);
    }
  }

  function handleInputMethodChange(method) {
    setIsTextInput(method === "text");
    setFile(null);
    setText("");
    setCustomQuery("");
    setAnalysisResult("");
    setError("");
  }

  if (!isBackendAvailable) {
    return (
      <div className="p-6 max-w-4xl mx-auto">
        <div className="bg-red-50 border border-red-200 rounded-lg p-4 text-red-600">
          Cannot connect to backend server. Please ensure it's running at {BACKEND_URL}
        </div>
      </div>
    );
  }

  return (
    <div className="p-6 max-w-4xl mx-auto bg-white shadow-lg rounded-lg">
      <h1 className="text-2xl font-bold mb-6">Document Analyzer</h1>

      <div className="flex space-x-4 mb-6">
        <button
          onClick={() => handleInputMethodChange("file")}
          className={`px-4 py-2 rounded transition-colors ${
            !isTextInput 
              ? "bg-blue-500 text-white" 
              : "bg-gray-200 text-gray-700 hover:bg-gray-300"
          }`}
        >
          Upload Document
        </button>
        <button
          onClick={() => handleInputMethodChange("text")}
          className={`px-4 py-2 rounded transition-colors ${
            isTextInput 
              ? "bg-blue-500 text-white" 
              : "bg-gray-200 text-gray-700 hover:bg-gray-300"
          }`}
        >
          Enter Text
        </button>
      </div>

      {error && (
        <div className="mb-6 p-4 bg-red-50 border border-red-200 rounded-lg text-red-600">
          {error}
        </div>
      )}

      {/* Analysis Category Dropdown */}
      <div className="mb-6">
        <label className="font-medium mr-2">Select Analysis Category:</label>
        <select
          className="p-2 border rounded"
          value={analysisCategory}
          onChange={(e) => setAnalysisCategory(e.target.value)}
        >
          <option value="summary">Summary</option>
          <option value="sentiment">Sentiment Analysis</option>
          <option value="keywords">Keywords Extraction</option>
          <option value="entity-recognition">Entity Recognition</option>
        </select>
      </div>

      {/* Custom Prompt Input */}
      {isTextInput ? (
        <div className="space-y-4">
          <textarea
            className="w-full p-3 border rounded min-h-[200px] focus:ring-2 focus:ring-blue-500 focus:border-transparent"
            placeholder="Enter text here for analysis..."
            value={text}
            onChange={(e) => setText(e.target.value)}
          />

          <div className="space-y-2">
            <label className="block font-medium">Custom Query (Optional)</label>
            <input
              type="text"
              className="w-full p-3 border rounded focus:ring-2 focus:ring-blue-500 focus:border-transparent"
              placeholder="Enter specific analysis question..."
              value={customQuery}
              onChange={(e) => setCustomQuery(e.target.value)}
            />
          </div>

          <button
            onClick={handleTextSubmit}
            disabled={isLoading || !text.trim()}
            className={`w-full py-2 rounded font-medium transition-colors ${
              isLoading || !text.trim()
                ? "bg-gray-300 text-gray-500 cursor-not-allowed"
                : "bg-blue-500 text-white hover:bg-blue-600"
            }`}
          >
            {isLoading ? "Analyzing..." : "Analyze Content"}
          </button>
        </div>
      ) : (
        <div
          {...getRootProps()}
          className={`border-2 border-dashed rounded-lg p-8 text-center cursor-pointer transition-colors ${
            isLoading 
              ? "bg-gray-50 border-gray-300" 
              : "border-blue-300 hover:border-blue-500 hover:bg-blue-50"
          }`}
        >
          <input {...getInputProps()} />
          {isLoading ? (
            <p className="text-gray-500">Processing file...</p>
          ) : (
            <>
              <p className="text-lg mb-2">Drop your file here, or click to select</p>
              <p className="text-sm text-gray-500">
                Supported formats: PDF, DOCX, TXT
              </p>
            </>
          )}
        </div>
      )}

      {/* Display Analysis Result */}
      {analysisResult && (
        <div className="mt-6">
          <h2 className="text-xl font-bold mb-2">Analysis Result</h2>
          <div className="p-4 bg-gray-50 border rounded-lg">
            <ReactMarkdown>{analysisResult}</ReactMarkdown>
          </div>
        </div>
      )}
    </div>
  );
};

export default DocumentAnalyzer;
