import React, { useState, useEffect } from "react";
import { useDropzone } from "react-dropzone";
import ReactMarkdown from 'react-markdown';
import { Card, CardHeader, CardTitle, CardContent } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Textarea } from '@/components/ui/textarea';
import { Select, SelectTrigger, SelectValue, SelectContent, SelectItem } from '@/components/ui/select';
import { Upload, FileText, Loader2 } from 'lucide-react';

const BACKEND_URL = "http://localhost:8000";

const DocumentAnalyzer = () => {
  const [file, setFile] = useState(null);
  const [text, setText] = useState("");
  const [analysisResult, setAnalysisResult] = useState("");
  const [customQuery, setCustomQuery] = useState("");
  const [analysisCategory, setAnalysisCategory] = useState("summary");
  const [isTextInput, setIsTextInput] = useState(false);
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState("");
  const [isBackendAvailable, setIsBackendAvailable] = useState(false);

  useEffect(() => {
    checkBackendHealth();
  }, []);

  async function checkBackendHealth() {
    try {
      const response = await fetch(`${BACKEND_URL}/health`);
      const data = await response.json();
      setIsBackendAvailable(data.status === "healthy");
    } catch (error) {
      setIsBackendAvailable(false);
      setError("Cannot connect to backend server. Please ensure it's running.");
    }
  }

  const { getRootProps, getInputProps } = useDropzone({
    onDrop: handleFileDrop,
    accept: {
      'application/pdf': ['.pdf'],
      'application/vnd.openxmlformats-officedocument.wordprocessingml.document': ['.docx'],
      'text/plain': ['.txt']
    },
    multiple: false
  });

  async function handleFileDrop(acceptedFiles) {
    try {
      setIsLoading(true);
      setError("");
      const uploadedFile = acceptedFiles[0];
      setFile(uploadedFile);

      const formData = new FormData();
      formData.append("file", uploadedFile);
      formData.append("query", customQuery.trim() || "Analyze the document");
      formData.append("category", analysisCategory);

      const response = await fetch(`${BACKEND_URL}/upload`, {
        method: "POST",
        body: formData,
      });

      if (!response.ok) {
        const errorData = await response.json().catch(() => ({}));
        throw new Error(errorData.detail || `Upload failed: ${response.statusText}`);
      }

      const data = await response.json();
      setAnalysisResult(data.result || data.extracted_text);
    } catch (error) {
      setError(`Error uploading file: ${error.message}`);
    } finally {
      setIsLoading(false);
    }
  }

  async function handleTextSubmit() {
    try {
      setIsLoading(true);
      setError("");

      if (!text.trim()) {
        throw new Error("Please enter some text to analyze");
      }

      const response = await fetch(`${BACKEND_URL}/analyze_text`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ 
          text, 
          query: customQuery.trim() || "Analyze this text and provide key insights",
          category: analysisCategory
        }),
      });

      if (!response.ok) {
        const errorData = await response.json().catch(() => ({}));
        throw new Error(errorData.detail || `Analysis failed: ${response.statusText}`);
      }

      const data = await response.json();
      setAnalysisResult(data.result);
    } catch (error) {
      setError(`Error analyzing text: ${error.message}`);
    } finally {
      setIsLoading(false);
    }
  }

  function handleInputMethodChange(method) {
    setIsTextInput(method === "text");
    setFile(null);
    setText("");
    setCustomQuery("");
    setAnalysisResult("");
    setError("");
  }

  if (!isBackendAvailable) {
    return (
      <Card className="max-w-4xl mx-auto">
        <CardContent className="p-6">
          <div className="bg-red-50 border border-red-200 rounded-lg p-4 text-red-600">
            Cannot connect to backend server. Please ensure it's running at {BACKEND_URL}
          </div>
        </CardContent>
      </Card>
    );
  }

  return (
    <Card className="max-w-4xl mx-auto">
      <CardHeader>
        <CardTitle>Document Analyzer</CardTitle>
      </CardHeader>
      <CardContent className="space-y-6">
        <div className="flex space-x-4">
          <Button
            variant={!isTextInput ? "default" : "outline"}
            onClick={() => handleInputMethodChange("file")}
          >
            <Upload className="w-4 h-4 mr-2" />
            Upload Document
          </Button>
          <Button
            variant={isTextInput ? "default" : "outline"}
            onClick={() => handleInputMethodChange("text")}
          >
            <FileText className="w-4 h-4 mr-2" />
            Enter Text
          </Button>
        </div>

        {error && (
          <div className="p-4 bg-red-50 border border-red-200 rounded-lg text-red-600">
            {error}
          </div>
        )}

        <div className="space-y-4">
          <div className="flex flex-col space-y-2">
            <label className="font-medium">Analysis Category</label>
            <Select value={analysisCategory} onValueChange={setAnalysisCategory}>
              <SelectTrigger>
                <SelectValue />
              </SelectTrigger>
              <SelectContent>
                <SelectItem value="summary">Summary</SelectItem>
                <SelectItem value="sentiment">Sentiment Analysis</SelectItem>
                <SelectItem value="keywords">Keywords Extraction</SelectItem>
                <SelectItem value="entity-recognition">Entity Recognition</SelectItem>
              </SelectContent>
            </Select>
          </div>

          <div className="flex flex-col space-y-2">
            <label className="font-medium">Custom Query (Optional)</label>
            <Input
              placeholder="Enter specific analysis question..."
              value={customQuery}
              onChange={(e) => setCustomQuery(e.target.value)}
            />
          </div>

          {isTextInput ? (
            <>
              <Textarea
                className="min-h-[200px]"
                placeholder="Enter text here for analysis..."
                value={text}
                onChange={(e) => setText(e.target.value)}
              />
              <Button
                className="w-full"
                onClick={handleTextSubmit}
                disabled={isLoading || !text.trim()}
              >
                {isLoading && <Loader2 className="w-4 h-4 mr-2 animate-spin" />}
                {isLoading ? "Analyzing..." : "Analyze Content"}
              </Button>
            </>
          ) : (
            <div
              {...getRootProps()}
              className="border-2 border-dashed rounded-lg p-8 text-center transition-colors hover:border-primary cursor-pointer"
            >
              <input {...getInputProps()} />
              {isLoading ? (
                <div className="flex flex-col items-center space-y-2">
                  <Loader2 className="w-6 h-6 animate-spin" />
                  <p className="text-muted-foreground">Processing file...</p>
                </div>
              ) : (
                <>
                  <Upload className="w-8 h-8 mx-auto mb-4 text-muted-foreground" />
                  <p className="text-lg mb-2">Drop your file here, or click to select</p>
                  <p className="text-sm text-muted-foreground">
                    Supported formats: PDF, DOCX, TXT
                  </p>
                </>
              )}
            </div>
          )}
        </div>

        {analysisResult && (
          <div className="mt-6">
            <h2 className="text-xl font-bold mb-2">Analysis Result</h2>
            <div className="p-4 bg-secondary/10 rounded-lg prose max-w-none">
              <ReactMarkdown>{analysisResult}</ReactMarkdown>
            </div>
          </div>
        )}
      </CardContent>
    </Card>
  );
};

export default DocumentAnalyzer;
