import React, { useState } from 'react';
import { Upload, FileText, Book, Activity } from 'lucide-react';
import { Alert, AlertDescription, AlertTitle } from '@/components/ui/alert';

const DocumentAnalyzer = () => {
  const [file, setFile] = useState(null);
  const [text, setText] = useState('');
  const [analysis, setAnalysis] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [activeTab, setActiveTab] = useState('upload');
  const [selectedCategory, setSelectedCategory] = useState('general');
  const [customQuery, setCustomQuery] = useState('');

  const handleFileUpload = async (event) => {
    const uploadedFile = event.target.files[0];
    setFile(uploadedFile);

    const formData = new FormData();
    formData.append('file', uploadedFile);

    setLoading(true);
    setError(null);

    try {
      const response = await fetch('http://localhost:8000/upload', {
        method: 'POST',
        body: formData,
      });

      const data = await response.json();
      setAnalysis(data.analysis);
      setText(data.text);
    } catch (err) {
      setError('Failed to upload and analyze file');
      console.error(err);
    } finally {
      setLoading(false);
    }
  };

  const analyzeText = async () => {
    setLoading(true);
    setError(null);

    try {
      const response = await fetch('http://localhost:8000/analyze', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          text,
          category: selectedCategory === 'general' ? null : selectedCategory,
          custom_query: customQuery || null,
        }),
      });

      const data = await response.json();
      setAnalysis(data);
    } catch (err) {
      setError('Failed to analyze text');
      console.error(err);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="container mx-auto p-6 max-w-6xl">
      <h1 className="text-3xl font-bold mb-8">Document Analyzer</h1>

      {/* Tabs */}
      <div className="flex mb-6 space-x-4">
        <button
          className={`flex items-center px-4 py-2 rounded-lg ${
            activeTab === 'upload'
              ? 'bg-blue-500 text-white'
              : 'bg-gray-100 hover:bg-gray-200'
          }`}
          onClick={() => setActiveTab('upload')}
        >
          <Upload className="w-4 h-4 mr-2" />
          Upload Document
        </button>
        <button
          className={`flex items-center px-4 py-2 rounded-lg ${
            activeTab === 'text'
              ? 'bg-blue-500 text-white'
              : 'bg-gray-100 hover:bg-gray-200'
          }`}
          onClick={() => setActiveTab('text')}
        >
          <FileText className="w-4 h-4 mr-2" />
          Enter Text
        </button>
      </div>

      {/* Input Section */}
      <div className="mb-8">
        {activeTab === 'upload' ? (
          <div className="border-2 border-dashed border-gray-300 rounded-lg p-8 text-center">
            <input
              type="file"
              onChange={handleFileUpload}
              className="hidden"
              id="file-upload"
              accept=".txt,.pdf,.docx"
            />
            <label
              htmlFor="file-upload"
              className="cursor-pointer flex flex-col items-center"
            >
              <Upload className="w-12 h-12 text-gray-400 mb-4" />
              <span className="text-gray-600">
                Click to upload or drag and drop
              </span>
              <span className="text-sm text-gray-500 mt-2">
                Supported formats: PDF, DOCX, TXT
              </span>
            </label>
            {file && (
              <div className="mt-4 text-sm text-gray-600">
                Selected file: {file.name}
              </div>
            )}
          </div>
        ) : (
          <textarea
            className="w-full h-48 p-4 border rounded-lg"
            value={text}
            onChange={(e) => setText(e.target.value)}
            placeholder="Enter or paste your text here..."
          />
        )}
      </div>

      {/* Analysis Options */}
      <div className="mb-8 space-y-4">
        <div>
          <label className="block text-sm font-medium mb-2">
            Analysis Category
          </label>
          <select
            className="w-full p-2 border rounded-lg"
            value={selectedCategory}
            onChange={(e) => setSelectedCategory(e.target.value)}
          >
            <option value="general">General Analysis</option>
            <option value="productivity">Productivity</option>
            <option value="career">Career</option>
            <option value="life_hacks">Life Hacks</option>
          </select>
        </div>

        <div>
          <label className="block text-sm font-medium mb-2">
            Custom Query (Optional)
          </label>
          <input
            type="text"
            className="w-full p-2 border rounded-lg"
            value={customQuery}
            onChange={(e) => setCustomQuery(e.target.value)}
            placeholder="Enter specific analysis question..."
          />
        </div>

        <button
          className="w-full bg-blue-500 text-white py-2 rounded-lg hover:bg-blue-600 disabled:bg-gray-300"
          onClick={analyzeText}
          disabled={loading || (!text && !file)}
        >
          {loading ? 'Analyzing...' : 'Analyze Content'}
        </button>
      </div>

      {/* Error Display */}
      {error && (
        <Alert variant="destructive" className="mb-6">
          <AlertTitle>Error</AlertTitle>
          <AlertDescription>{error}</AlertDescription>
        </Alert>
      )}

      {/* Results Display */}
      {analysis && (
        <div className="space-y-6">
          <div className="bg-white p-6 rounded-lg shadow-lg">
            <h2 className="text-xl font-semibold mb-4 flex items-center">
              <Book className="w-5 h-5 mr-2" />
              Key Insights
            </h2>
            <ul className="list-disc pl-6 space-y-2">
              {analysis.insights?.map((insight, index) => (
                <li key={index} className="text-gray-700">
                  {insight}
                </li>
              ))}
            </ul>
          </div>

          <div className="bg-white p-6 rounded-lg shadow-lg">
            <h2 className="text-xl font-semibold mb-4 flex items-center">
              <Activity className="w-5 h-5 mr-2" />
              Recommendations
            </h2>
            <ul className="list-disc pl-6 space-y-2">
              {analysis.recommendations?.map((rec, index) => (
                <li key={index} className="text-gray-700">
                  {rec}
                </li>
              ))}
            </ul>
          </div>

          {analysis.quotes && (
            <div className="bg-white p-6 rounded-lg shadow-lg">
              <h2 className="text-xl font-semibold mb-4">Important Quotes</h2>
              <div className="space-y-4">
                {analysis.quotes.map((quote, index) => (
                  <blockquote
                    key={index}
                    className="border-l-4 border-blue-500 pl-4 italic text-gray-700"
                  >
                    {quote}
                  </blockquote>
                ))}
              </div>
            </div>
          )}
        </div>
      )}
    </div>
  );
};

export default DocumentAnalyzer;
